classdef VideoLabelerAutomation < vision.labeler.AutomationAlgorithm\n    properties(Constant)\n        Name = 'Advanced Video Segmentation';\n        Description = 'Multi-technique video object segmentation';\n        UserDirections = 'Draw initial annotation on first frame';\n    end\n    \n    properties\n        Algorithm\n    end\n    \n    methods\n        function this = VideoLabelerAutomation()\n            this.Algorithm = AdvancedVideoSegmentationAlgorithm();\n        end\n        \n        function isValid = checkSetup(this)\n            isValid = this.Algorithm.checkSetup();\n        end\n        \n        function automatedLabels = run(this, videoReader, selectedLabelName, existingLabels, currentFrame)\n            automatedLabels = this.Algorithm.run(videoReader, selectedLabelName, existingLabels, currentFrame);\n        end\n    end\nend